import React, { useState } from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom';

const TopNavbar = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('bg-dark', darkMode);
    document.body.classList.toggle('text-light', darkMode);
    document.body.classList.toggle('bg-light', !darkMode);
    document.body.classList.toggle('text-dark', !darkMode);
  };

  const toggleFullScreen = () => {
    if (document.fullscreenElement) {
      document.exitFullscreen();
    } else {
      document.documentElement.requestFullscreen();
    }
  };

  return (
    <nav className={`navbar navbar-expand-lg ${darkMode ? 'navbar-dark bg-dark' : 'navbar-light bg-light'}`}>
      <Link className="navbar-brand" to="#">Your Logo</Link>
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav"
              aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav ml-auto">
          <li className="nav-item">
            <Link className="nav-link" to="#">Login</Link>
          </li>
          <li className="nav-item">
            <button className={`btn btn-outline-${!darkMode ? 'light' : 'dark'}`} onClick={toggleDarkMode}>
              Dark Mode Toggle
            </button>
          </li>
          <li className="nav-item">
            <button className={`btn btn-outline-${darkMode ? 'light' : 'dark'}`} onClick={toggleFullScreen}>
              Full Screen Toggle
            </button>
          </li>
          <li className="nav-item dropdown">
            <Link className="nav-link dropdown-toggle" to="#" id="messagesDropdown" role="button"
               data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              Messages
            </Link>
            <div className="dropdown-menu" aria-labelledby="messagesDropdown">
              {/* Your messages dropdown content goes here */}
            </div>
          </li>
          <li className="nav-item dropdown">
            <Link className="nav-link dropdown-toggle" to="#" id="notificationsDropdown" role="button"
               data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              Notifications
            </Link>
            <div className="dropdown-menu" aria-labelledby="notificationsDropdown">
              {/* Your notifications dropdown content goes here */}
            </div>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default TopNavbar;
