import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import Table from './Table';

const Login = () => {
  const [username, setUsername] = useState("");
  const [userpassword, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  const submitForm = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:8080/Users',{
      userName: username,
      password: userpassword,
    });
  }
  
  return (
      <>
          <div className="row justify-content-center">
              <div className="col-md-6">
              <div className="card bg-dark text-light">
                  <div className="card-header">
                  <h3 className="text-center">Login<Table /></h3>
                  </div>
                  <div className="card-body">
                  <Form>
                      <Form.Group className="form-group">
                      <Form.Label htmlFor="username">Email address:</Form.Label>
                      <Form.Control type="text" className="form-control" id="username" placeholder="Enter username" />
                      </Form.Group>
                      <Form.Group className="form-group">
                      <Form.Label htmlFor="password">Password:</Form.Label>
                      <Form.Control type="password" className="form-control" id="password" placeholder="Enter password" />
                      </Form.Group>
                      <Button type="submit" className="btn btn-primary btn-block">Login</Button>
                      {/* {error && <p style={{ color: 'red' }}>Error: {error.message}</p>} */}
                    </Form>
                  </div>
              </div>
              </div>
          </div>
      </>
  );
};

export default Login;
